import { Player } from "./Player";

export class Game {
  player: Player;

  constructor(player: Player) {
    this.player = player;
  }
}

export class GameEvents {
  description!: string;
  day!: number;
  effect: any;

  constructor(eventConfig: any, day: number) {
    this.day = day;
    this.description = eventConfig.description;
    this.effect = eventConfig.effect;
  }

  setEffect(effect: any) {
    this.effect = effect;
    const subject = this.effect.subject;
    const target = this.effect.target;
    this.description = this.description.replace(/{{subject}}/g, subject.name).replace(/{{target}}/g, target?.name);
  }
}

import { Item } from "./Item";

export class Inventory {
  items: Item[] = [];

  constructor() {
    this.items = [];
  }

  // Calculate total value of the inventory
  get totalValue() {
    return this.items.reduce((acc, item) => acc + (item.cost! * item.quantity!), 0);
  };

  // Add an item to the inventory, if it already exists, increase the quantity
  addItem(item: Item) {
    let existingItem = this.items.find(i => i.name === item.name);
    if (existingItem) {
      existingItem.quantity! += item.quantity!;
    } else {
      this.items.push(item);
    }
  };

  // Remove an item from the inventory, if it exists, decrease the quantity, if the quantity is 0, remove the item
  removeItem(item: Item) {
    let existingItem = this.items.find(i => i.name === item.name);
    if (existingItem) {
      existingItem.quantity! -= item.quantity!;
      if (existingItem.quantity! <= 0) {
        this.items = this.items.filter(i => i.name !== item.name);
      }
    }
  };

  //Update item in inventory
  updateItem(item: Item) {
    const existingItem = this.items.find(i => i.name === item.name);
    const qty = item.quantity;
    if (existingItem) {
      if (qty === 0) {
        this.items = this.items.filter(i => i.name !== item.name);
      } else {
        existingItem.quantity = item.quantity;
      }
    } else {
      this.items.push(item);
    }
  };

}

export class Item {
  id?: number;
  name: string;
  cost?: number;
  value: number;
  description: string;
  icon?: string;
  quantity?: number;

  constructor(name: string, value: number, description: string, quantity?: number, id?: number) {
    this.name = name;
    this.value = value;
    this.cost = this.value; //item cost is initialized at 100% of value
    this.description = description;
    this.quantity = quantity || 0;
  }
}

import ICity from "../utils/ICity.interface";
import { Inventory } from "./Inventory";

export class Location implements ICity {
  id!: number;
  name!: string;
  inventory?: Inventory;
  x!: number;
  y!: number;
  size!: number;
  money!: number;

  constructor(id: number, name: string, x: number, y: number, size: number) {
    this.id = id;
    this.name = name;
    this.x = x;
    this.y = y;
    this.size = size;
  }

  setInventory(inventory: Inventory) {
    this.inventory = inventory;
  };

  setMoney(money: number) {
    this.money = money;
  };

}

import { Inventory } from "./Inventory";

export class Player {
